mapper.select:
select sg.id,sc.`key` areaName,ss.`key` schoolName,sr.`key` gradeName,sl.`key` className from sys_grade sg LEFT JOIN sys_config sc on sg.area_id=sc.`value` and sc.type='campusType' LEFT JOIN sys_config ss on sg.school_id=ss.`value` and ss.type='schoolName' LEFT JOIN sys_config sr on sg.grade_id=sr.`value` and sr.type='gradeName' LEFT JOIN sys_config sl on sg.class_id=sl.`value` and sl.type='className'
where sc.`key` like concat('%',#{areaName},'%')
limit #{offset}, #{limit}

select s.user_id, s.username, s.password, s.salt, s.sex, s.age, s.email, s.mobile, s.status, s.create_user_id, s.create_time, s.area_id, s.school_id, s.grade_id, s.class_id, sr.role_name from sys_user s LEFT JOIN sys_user_role su on su.user_id=s.user_id LEFT JOIN sys_role sr on su.role_id=sr.role_id
and s.`area_id` = #{areaId}
and s.`school_id` = #{schoolId}
and s.`grade_id` = #{gradeId}
and s.`class_id` = #{classId}
and s.username like concat('%',#{userName},'%')
and s.area_id is null
order by ${sidx} ${order}
order by s.user_id desc
limit #{offset}, #{limit}

select count(1) from
sys_user s
LEFT JOIN
sys_user_role su on su.user_id=s.user_id
LEFT JOIN sys_role sr on su.role_id=sr.role_id


		and s.`area_id` = #{areaId}


		and s.`school_id` = #{schoolId}


		and s.`grade_id` = #{gradeId}


		and s.`class_id` = #{classId}


		and s.username like concat('%',#{userName},'%')


		and s.area_id is null

mapper.update:
update sys_user set `area_id` = #{areaId}, `school_id` = #{schoolId},`grade_id` = #{gradeId}, class_id = #{classId}
where user_id in

	#{userId}
